def wrap(cls, message_parts):
        """Wraps exceptions in the context with :exc:`MalformedMessage`."""
        try:
            yield
        except BaseException as exception:
            __, __, tb = sys.exc_info()
            reraise(cls, cls(exception, message_parts), tb)

